import org.junit.Assert;
import org.junit.Test;
import com.tls.base64_url.*;
import org.bouncycastle.util.encoders.DecoderException;

public class TestBase64 {
    @Test
    public void base64() {
        byte[] res = base64_url.base64EncodeUrl("123".getBytes());
        Assert.assertArrayEquals("MTIz".getBytes(), res);

        res = base64_url.base64DecodeUrl(res);
        Assert.assertArrayEquals("123".getBytes(), res);

        res = base64_url.base64EncodeUrl("1".getBytes());
        Assert.assertArrayEquals("MQ__".getBytes(), res);

        res = base64_url.base64DecodeUrl(res);
        Assert.assertArrayEquals("1".getBytes(), res);
    }

    @Test
    public void failed() {
        try {
            base64_url.base64DecodeUrl("123".getBytes());
            Assert.fail();
        } catch (Exception e) {
            Assert.assertEquals(DecoderException.class, e.getClass());
        }
    }

    @Test
    public void testLong() {
        byte[] res = base64_url.base64EncodeUrl("".getBytes());
        Assert.assertArrayEquals("".getBytes(), res);

        res = base64_url.base64DecodeUrl(res);
        Assert.assertArrayEquals("".getBytes(), res);
    }
}
